// data/lessonsData.js
import ConditionIntro from "../pages/lessons/ConditionIntro";

const lessonsData = {
    java: {
        theme: "Java",
        levels: [
            {
                name: "Débutant",
                courses: [
                    {
                        id: 1,
                        title: "Introduction à Java",
                        type: "cours",
                        content: "Java est un langage orienté objet très populaire...",
                    },
                    {
                        id: 2,
                        title: "Variables et types",
                        type: "cours",
                        content: "Les variables en Java permettent de stocker des données...",
                    },
                    {
                        id: 3,
                        title: "Jeu : Quiz Java",
                        type: "jeu",
                        content: {
                            questions: [
                                {
                                    question: "Quel est le type de variable pour un nombre entier ?",
                                    options: ["int", "String", "boolean"],
                                    answer: 0,
                                },
                                {
                                    question: "Quelle est la bonne façon de déclarer une variable ?",
                                    options: ["int x = 5;", "var x 5;", "x := 5;"],
                                    answer: 0,
                                },
                            ],
                        },
                    },
                ],
            },
            // niveaux intermédiaire, etc.
        ],
    },

    git: {
        theme: "Git",
        levels: [
            {
                name: "Débutant",
                courses: [
                    { id: 1, title: "Qu'est-ce que Git ?", type: "cours" },
                    { id: 2, title: "Git init / Git add", type: "cours" },
                ],
            },
        ],
    },

    algorithme: {
        theme: "Algorithme",
        levels: [
            {
                name: "Débutant",
                courses: [
                    { id: 1, title: "Qu’est-ce qu’un algorithme ?", type: "cours",  component: "AlgorithmeIntro" },
                    { id: 2, title: "Conditions : si… alors… c’est facile ! ", type: "cours",  component: "ConditionIntro" },
                    { id: 3, title: "La boucle : répéter une action facilement  ", type: "cours",  component: "BoucleIntro" },
                    { id: 4, title: "Les variable : répéter une action facilement  ", type: "cours",  component: "VariableIntro" },
                    { id: 5, title: "Function : répéter une action facilement  ", type: "cours",  component: "FunctionIntro" },
                    { id: 6, title: "LogicGame : répéter une action facilement  ", type: "jeu",  component: "LogicGameIntro" }
                ],

            },
            {
                name: "Débutant + – Premiers pas en code",
                courses: [
                    { id: 7, title: "Afficher un message avec console.log", type: "cours",  component: "ConsoleLogIntro" },
                    { id: 8, title: "Let Variables — Comprendre les variables en JavaScript", type: "cours",  component: "LetVariablesIntro" },
                    { id: 9, title: "If Condition — Prendre des décisions", type: "cours",  component: "IfConditionIntro" },
                    { id: 10, title: "Écris ton premier algorithme !", type: "jeu",  component: "FirstAlgorithmGame" },
                ],

            },
            {
                name: "Débutant ++ – Les notions",
                courses: [
                    { id: 11, title: "Mini-jeu pour inverser deux variables", type: "jeu",  component: "InverserVariables" },
                    { id: 12, title: "Calculer le prix HT et le prix TTC", type: "jeu",  component: "InverserVariables" },

                ],

            },
        ],
    },

    // Tu peux ajouter HTTP, Spring, etc. de la même manière
};

export default lessonsData;
